  var dataUpdate = data
  if(filter)
       dataUpdate = data.filter(item=> item.subCategories.includes(filter))
  if(max)
    dataUpdate = data.filter(item=> item.newPrice > max) 


  console.log(dataUpdate)
   return (
    <div className="list">
