import { Link } from "react-router-dom";
import "./Card.scss";

const Card = ({ item }) => {
   return (
    <Link to={`/product/${item.id}`} className="link">
      <div className="card">
        <div className="image">
          {item.isNew && <span>new product</span>}
          <img
            className="img"
            src={
             item?.img1
            }
            alt=""
          />
          <img
            className="img2"
            src={
               item?.img2?.data?.url
            }
            alt=""
          />
        </div>
        <span className="type">{item.title}</span>
        <div className="prices">
          <span>${item.oldPrice || item.price + 20}</span>
          <span>${item.price}</span>
        </div>
      </div>
    </Link>
  );
};

export default Card;
