import { Link } from "react-router-dom";
import "./Card.scss";

const Card = ({ item }) => {
   return (
    <Link to={`/product/${item.id}`} className="link">
      <div className="card">
        <div className="image">
          {item.attributes.isNew && <span>new product</span>}
          <img
            className="img"
            src={
              process.env.REACT_APP_API_URL_IMG +
              item.attributes?.img?.data?.attributes?.url
            }
            alt=""
          />
          <img
            className="img2"
            src={
              process.env.REACT_APP_API_URL_IMG +
              item.attributes?.img2?.data?.attributes?.url
            }
            alt=""
          />
        </div>
        <span className="type">{item.attributes.title}</span>
        <div className="prices">
          <span>${item.oldPrice || item.attributes.price + 20}</span>
          <span>${item.attributes.price}</span>
        </div>
      </div>
    </Link>
  );
};

export default Card;
